import { useState } from "react";
import styles from './App.module.css';

function App() {

  let [count,setCount] = useState(0);
  const cardValues = [10,20,30,40,50];
  const handleIncrement = (num) => {
    //alert('called');
    setCount((prevCount)=>prevCount+num);
  }

  const handleDecrement = (num) => {
    setCount((prevCount) => prevCount - num);
  }

  const reset = () => {
    setCount(0);
  }
  return (
    <>
    <div className="container">
        <h2>Count : {count}</h2>
        <button onClick={() => handleIncrement(5)}>Increment</button>
        <button onClick={() => handleDecrement(5)}>Decrement</button>
        <button onClick={reset}>Reset</button>
    </div>
    <IsOddorEvent count={count}/>
    <RandomCard cardValues={cardValues}/>
    </>
  );
}

const IsOddorEvent = ({count}) => {
  return <h3>Number is {count % 2 === 0 ? 'Even':'Odd'}</h3>
}

const RandomCard = ({cardValues}) => {
  return (
    <div>
        {
          cardValues.map( (cardValue,index) => {
            return <div className={styles.box} key={index}>{cardValue}</div>
          })
        }
    </div>
  )
}

export default App;
