import { useState } from "react";
function App() {

  const [count,setCount] = useState(0);

  const handleIncrement = () => {
    //alert('called');
    setCount(count++);
  }
  return (
    <div className="container">
        <h2>Count : {count}</h2>
        <button onClick={handleIncrement}>Increment</button>
        <button>Decrement</button>
        <button>Reset</button>
    </div>
  );
}

export default App;
